 p_id=>wwv_flow_api.id(11043999779199887)
,p_process_sequence=>10
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'add package'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'     n_volume number := V(''P6_LENGTH'') * V(''P6_WIDTH'') * V(''P6_HEIGHT'');',
'     row_parcel parcels%rowtype;',
'     n_exists number := 0;',
'     n_parcel_count delivery_details.parcel_count%type := 1;',
'     n_parcel_value delivery_details.value%type;',
'     n_seq number := 0;',
'',
'     cursor cur_parcels',
'     is',
'     select code,name,price',
'     from parcels',
'     where length * width * height > n_volume',
'     order by LENGTH * width * height;',
'',
'',
'',
'begin ',
'',
'        if not apex_collection.collection_exists(''PARCELS'') then ',
'',
'              apex_collection.create_collection(''PARCELS'');',
'',
'        end if;',
'',
'       for i in cur_parcels loop',
'             ',
'             ',
'             row_parcel.code := i.code;',
'             row_parcel.name := i.name;',
'             row_parcel.price := i.price;',
'            exit when i.code is not null;',
'             ',
'',
'         end loop;',
'',
'',
'',
'',
'',
'        select count(*)',
'          into n_exists',
'        from apex_collections',
'        WHERE collection_name = ''PARCELS'' AND c001 like row_parcel.code;',
'',
'        IF n_exists = 1 THEN',
'',
'            select n001,seq_id',
'              into n_parcel_count,n_seq',
'            from apex_collections',
'            where collection_name = ''PARCELS'' and c001 like row_parcel.code',
'            FETCH FIRST 1 ROWS ONLY;',
'',
'               n_parcel_count := n_parcel_count + 1;',
'               n_parcel_value := row_parcel.price * n_parcel_count;',
'',
'',
'             APEX_COLLECTION.UPDATE_MEMBER (',
'            p_collection_name => ''PARCELS'',',
'            p_seq =>             n_seq,',
'            p_c001 =>            row_parcel.code,',
'            p_c002 =>            row_parcel.name,',
'            p_n001 =>            n_parcel_count,',
'            p_n002 =>            row_parcel.price,',
'            p_n003 =>            n_parcel_value',
'          );',
'',
'        else',
'        ',
'          apex_collection.add_member(',
'            p_collection_name => ''PARCELS'',',
'            p_c001 =>            row_parcel.code,',
'            p_c002 =>            row_parcel.name,',
'            p_n001 =>            n_parcel_count,',
'            p_n002 =>            row_parcel.price,',
'            p_n003 =>            row_parcel.price',
'          );',
'',
'        end if;',
'',
' ',
'exception',
'',
'    when others then',
'        raise_application_error(-20001,''Wystąpił nieoczekiwany błąd!'');',
'        ',
'END;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(11027554140199873)
);
